#!/usr/bin/env node

var fs = require('fs');
var path = require('path');
var jsbeautify = path.join(process.env.TM_BUNDLE_SUPPORT, 'js');
var beautify_html = require(jsbeautify).html;

var opts = {
	'preserve-newlines': true,
};

if (process.env.TM_SOFT_TABS == 'no') {
	opts.indent_size = 1;
	opts.indent_char = '\t';
} else {
	opts.indent_size = parseInt(process.env.TM_TAB_SIZE, 10);
}

var content = '';

process.stdin.setEncoding('utf8');

process.stdin.on('readable', function() {
  var chunk = process.stdin.read();
  if (chunk !== null) {
    content += chunk;
  }
});

process.stdin.on('end', function() {
  process.stdout.write(beautify_html(content, opts));
});
